import { Component } from "react";
import './mortgagecalculator.css';

class MortgageCalculator extends Component{

    constructor(props) {
        super(props);
        this.state = {
            monthlyPayment: '',
            loanamnt: '',
            intrate: '',
            numOfYears: '',
        };
        // Bind handleCalculation method to the component instance
        this.handleCalculation = this.handleCalculation.bind(this);
    }

    handleCalculation = (event) => {
        event.preventDefault(); // Prevent form submission
        const { loanamnt, intrate, numOfYears } = this.state;
    
        // Convert interest rate to decimal and number of years to months
        const monthlyRate = parseFloat(intrate) / 100 / 12;
        const numOfMonths = parseFloat(numOfYears) * 12;
    
        // Calculate monthly payment
        let monthlyPayment = (parseFloat(loanamnt) * monthlyRate) / (1 - Math.pow(1 + monthlyRate, -numOfMonths));
    
        // Format monthly payment to display only two decimal places
        monthlyPayment = parseFloat(monthlyPayment.toFixed(2));
    
        // Update state with the calculated monthly payment
        this.setState({ monthlyPayment });
    };

    handleInputChange = (event) => {
        const { name, value } = event.target;
        this.setState({ [name]: value });
    };

    render(){
        return(
            <>
            <div className="mortgagecalculatorarea">
            <form onSubmit={this.handleCalculation}>
                <div className="morgagecalculatorinput"><label>Loan Amount: <input type="text" name="loanamnt" value={this.state.loanamnt} onChange={this.handleInputChange} placeholder="Loan Amount"/></label></div>
                <div className="morgagecalculatorinput"><label>Number of Years: <input type="text" name="numOfYears" value={this.state.numOfYears} onChange={this.handleInputChange} placeholder="Number of years"/></label></div>
                <div className="morgagecalculatorinput"><label>Interest Rate: <input type="text" name="intrate"  value={this.state.intrate} onChange={this.handleInputChange} placeholder="Interest Rate in%"/></label></div>
                <div className="morgagecalculatorinput"><label>Monthly Payment: ${this.state.monthlyPayment} </label></div>
                <input type="submit" value="Calculate Monthly Payment"/>
            </form>
            </div>
            </>
        )
    }
    
}

export default MortgageCalculator;
