import React, { Component } from "react";
import './rateandreview.css';

class RateAndReview extends Component {
    constructor(props) {
        super(props);
        this.state = {
            rating:"",
            review:"",
            username:"",
        };
    }

    fetchData = async () => {
        try {
            const response = await fetch('http://127.0.0.1:5000/api/data');
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const responseData = await response.json();
            return responseData.message;
        } catch (error) {
            console.error('Error fetching data:', error);
            return null;
        }
    };

    componentDidMount() {
        this.fetchData();
    }

    handleInputChange = (event) => {
        const { name, value } = event.target;
        this.setState({ [name]: value });
    };

    render(){
        const { username } = this.state;
        return(
            <>
                <div>
                    <h2>Rating your Agent: {username}</h2>
                    <label>Rating: </label><input type="text" name="rating" value={this.state.rating} placeholder="Rating" className="loginTextBox" onChange={this.handleInputChange}/>
                    <label>Review: </label><input type="text" name="review" value={this.state.review} placeholder="Review" className="loginTextBox" onChange={this.handleInputChange}/>
                </div>
            </>
        )
    }


}

export default RateAndReview;
