import React, { Component } from "react";
import { Link } from "react-router-dom";
import './sellerlistings.css';
import SellerListingsView from "./sellerlistingsview";

class SellerListings extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username:"",
        };
    }

    state = {
        listings:[]
    };

    componentDidMount() {
        this.setState({
            username: window.location.href.split('/')[4]
        });
    
        fetch('http://127.0.0.1:5000/api/login')
          .then(response => response.json())
          .then(data => {
              this.setState({ 
                  listings: data || [], // Ensure data is an array or default to empty array
              });
          })
          .catch(error => console.error('Error fetching listings:', error));
    }

    handleViewListing = (listingid) => {
        window.location.href = `/viewlistingpage/${listingid}`
    };

    // *** Not sure how to change the filteredListings check below
    render(){
        const { listings } = this.state;
        return(
            <>
            <div className="sellerlisting-area">
                <h1 className="sellerlisting-header">My Listings</h1>
                <div className="sellerlisting-container">
                    {listings && listings.map(listing => (
                        <SellerListingsView key={listing.id} 
                          listing={listing} 
                          onView={() => this.handleViewListing(listing.id)}
                        />
                    ))}
                </div>
            </div>
            </>
        )
    }
    
}

export default SellerListings;
