import { Component } from "react";


class BuyerSearch extends Component{

    constructor(props) {
        super(props);
        this.state = {
            status:"Unsold",
        };
    }
    
    handleSubmit = async (event) => {
        event.preventDefault();
        const { userType, username, password } = this.state;
        const success= await this.login(userType, username, password);
        //console.log("1) username: " + username + "    2) password: " + password + "    3) userType: " + userType)
        if (success && (userType === "sysadmin")) {
            window.location.href = '/sahomepage';
        } else if (success && (userType === "REA")) {
            window.location.href = `/reahomepage/${username}`;
        }  else if (success && (userType === "buyer")) {
            window.location.href = `/buyerhomepage/${username}`;
        } else {
            this.setState({ errorMessage: "Login failed. Please try again." });
        }
    };

    handleInputChange = (event) => {
        const { name, value } = event.target;
        this.setState({ [name]: value });
    };

    render(){
        return(
            <>
            <div className="searchpagearea">
                <h2>Search for Listing</h2>
                <form onSubmit={this.handleSubmit}>
                    <div className="searchPageFlexBox">
                        <label className="searchPageFlexItem">
                        <input type="radio" name="status" value="Unsold" checked={this.state.status === 'Unsold'} onChange={this.handleInputChange}/>Unsold</label>
                        <label className="searchPageFlexItem">
                        <input type="radio" name="status" value="Sold" checked={this.state.status === 'Sold'} onChange={this.handleInputChange}/>Sold</label> 
                    </div>
                </form>
            </div>
            </>
        )
    }
}

export default BuyerSearch;
