import { Component } from "react";

class MortgageCalculator extends Component{

    constructor(props) {
        super(props);
        this.state = {
            monthlyPayment: '',
            loanamnt: '',
            intrate: '',
            numOfYears: '',
        };
        // Bind handleCalculation method to the component instance
        this.handleCalculation = this.handleCalculation.bind(this);
    }

    handleCalculation = (event) => {
        event.preventDefault(); // Prevent form submission
        const { loanamnt, intrate, numOfYears } = this.state;
    
        // Convert interest rate to decimal and number of years to months
        const monthlyRate = parseFloat(intrate) / 100 / 12;
        const numOfMonths = parseFloat(numOfYears) * 12;
    
        // Calculate monthly payment
        const monthlyPayment = (parseFloat(loanamnt) * monthlyRate) / (1 - Math.pow(1 + monthlyRate, -numOfMonths));
    
        // Update state with the calculated monthly payment
        this.setState({ monthlyPayment });
    };

    handleInputChange = (event) => {
        const { name, value } = event.target;
        this.setState({ [name]: value });
    };

    render(){
        return(
            <>
            <form onSubmit={this.handleCalculation}>
                <label>Loan Amount: <input type="text" name="loanamnt" value={this.state.loanamnt} onChange={this.handleInputChange}/></label>
                <label>Number of Years: <input type="text" name="numOfYears" value={this.state.numOfYears} onChange={this.handleInputChange}/></label>
                <label>Interest Rate: <input type="text" name="intrate"  value={this.state.intrate} onChange={this.handleInputChange}/></label>
            </form>
            </>
        )
    }
    
}

export default MortgageCalculator;
