import React, { Component } from "react";
import { Link } from "react-router-dom";
import './sellerlistings.css';
import SellerListingsView from "./sellerlistingsview";

class SellerListings extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username:"",
            listings:[],
        };
    }

    componentDidMount() {
        this.setState({
            username: window.location.href.split('/')[4]
        });
    
        this.getListings();
    }

    getListings = async (username) => {
        try {
            const response = await fetch('http://127.0.0.1:5000/api/favourite-listings', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ username })
            });
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const jsonresponse = await response.json();
            // Assuming the server returns an array of listings
            return jsonresponse.listings || []; // Return the listings array or an empty array if not present
        } catch (error) {
            console.error('Error fetching listings:', error);
            return []; // Return an empty array if there's an error
        }
    };

    handleViewListing = (listingid) => {
        window.location.href = `/viewlistingpage/${listingid}`
    };

    // *** Not sure how to change the filteredListings check below
    render(){
        const { listings } = this.state;
        /*return(
            <>
            <div className="sellerlisting-area">
                <h1 className="sellerlisting-header">My Listings</h1>
                <div className="sellerlisting-container">
                    {listings && listings.map(listing => (
                        <SellerListingsView key={listing.id} 
                          listing={listing} 
                          onView={() => this.handleViewListing(listing.id)}
                        />
                    ))}
                </div>
            </div>
            </>
        )*/
        return(
            <>
            <div className="sellerlisting-area">
                <h1 className="sellerlisting-header">My Listings</h1>
                <div className="sellerlisting-container">
                    <table>
                        <thead>
                            <td>Address</td>
                            <td>Agent Username</td>
                            <td>Status</td>
                            <td>Views</td>
                            <td>Favourites</td>
                        </thead>
                    </table>
                </div>
            </div>
            </>
        )
    }
    
}

export default SellerListings;
