import { Component, useState, useEffect } from "react";
import React from "react";
import { Link, useParams } from "react-router-dom";
import './editprofile.css';

//should be placed in a class
class EditProfile extends Component{
    constructor(props) {
        super(props);
        this.state = {
            data: '',
            username: '',
            password: '',
            cpassword: '',
            newUsername: '',
        };
    }

    fetchData = async () => {
        try {
            const response = await fetch('http://127.0.0.1:5000/api/data');
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const responseData = await response.json();
            setData(responseData.message);
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };

    updateAccount = async () => {
        try {
            const response = await fetch('http://127.0.0.1:5000/api/update-user-details', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({username, password,newUsername})
            });
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const responseData = await response.json();
            return responseData.success;
        } catch (error) {
            console.error('Error logging in:', error);
            return null;
        }
    }

    componentDidMount() {
        this.fetchData();
    }

    handleSubmit = async (event) => {
        event.preventDefault();
        event.preventDefault();
        const { userType, username, password, cpassword } = this.state;
        if(password != cpassword){
            this.setState({ createAccountmessage: "Passwords do not match"});
            return;
        }

        const success = await this.updateAccount(userType, username, password, cpassword);
        if (success) {
            this.setState({ createAccountmessage: "Account successfully updated" });
        }else{
            this.setState({ createAccountmessage: "Username taken" });
        }
    };

    handleInputChange = (event) => {
        const { name, value } = event.target;
        this.setState({ [name]: value });
    };

    render(){
        return(
            <>
                <div>
                    <form className="updateAccTextArea" onSubmit={handleSubmit}>
                        <div className="loginTextBoxes">
                        <p className="caText">New Username:</p>
                        <input type="text" value={this.state.newUsername} onChange={this.handleInputChange} name="username" placeholder="Username" className="caTextBox"/>
                        <p className="caText">New Password:</p>
                        <input type="password" value={this.state.password} onChange={this.handleInputChange} name="password" placeholder="Password" className="caTextBox"/>
                        <p className="caText">Confirm new Password:</p>
                        <input type="password" value={this.state.cpassword} onChange={this.handleInputChange} name="cpassword" placeholder="Confirm Password" className="caTextBox"/>
                        </div>
                        <div className="preLoginAdditionalFunctions">
                            <Link to='/sahomepage'><input type="submit" value="Update" className="loginSubmit"/></Link>
                        </div>
                    </form>
                </div>
            </>
        )

}
}

export default EditProfile;
