import React, { Component } from "react";
import './rateandreview.css';

class RateAndReview extends Component {
    constructor(props) {
        super(props);
        this.state = {
            rating:"",
            review:"",
            username1:"",
            username2:"",
        };
    }

    handleInputChange = (event) => {
        const { name, value } = event.target;
        this.setState({ [name]: value });
    };

    handleSubmit = async (event) => {
        event.preventDefault();
        const { review,rating,username } = this.state;
        const success= await this.submitRandR(review,rating,username);
    };

    submitRandR = async (rating,review,username1,username2) => {
        try {
            const response = await fetch('http://127.0.0.1:5000/api/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ rating,review,username1,username2 })
            });
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const jsonresponse = await response.json(); // Assuming the server returns a boolean value
            return jsonresponse.success;
        } catch (error) {
            console.error('Error logging in:', error);
            return false;
        }
    };

    render(){
        const { username1 , username2 } = this.state;
        return(
            <>
                <div>
                    
                    <h2>Hello {username1}, please rate and review your Agent: {username2}</h2>
                    <form onSubmit={this.handleRatingSubmit}>
                        <label>Rating: </label>
                        <input type="text" name="rating" value={this.state.rating} placeholder="Rating" className="loginTextBox" onChange={this.handleInputChange}/>
                        <input type="submit">Confirm Rating</input>
                    </form>
                    <form onSubmit={this.handleReviewSubmit}>
                        <label>Review: </label>
                        <input type="text" name="review" value={this.state.review} placeholder="Review" className="loginTextBox" onChange={this.handleInputChange}/>
                        <input type="submit">Confirm Review</input>
                    </form>
                </div>
            </>
        )
    }


}

export default RateAndReview;
