import React, { useState } from 'react';

// Popup Form component
const PopupForm = ({ onSubmit, onClose }) => {
  const [profileName, setProfileName] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(profileName);
    setProfileName(''); // Clear input after submission
  };

  return (
    <div className="popup">
      <div className="popup-content">
        <span className="close" onClick={onClose}>&times;</span>
        <h2>Create Profile</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={profileName}
            onChange={(e) => setProfileName(e.target.value)}
            placeholder="Enter profile name"
            required
          />
          <button type="submit">Submit</button>
        </form>
      </div>
    </div>
  );
};

// ProfileTable component
const ProfileTable = ({ profileName }) => {
  return (
    <div>
      <h2>{profileName}</h2>
      <table>
        {/* Table contents */}
      </table>
    </div>
  );
};

// component
const PTPopup = () => {
  const [showPopup, setShowPopup] = useState(false);
  const [profileName, setProfileName] = useState('');
  const [profileTables, setProfileTables] = useState([]);

  const openPopup = () => {
    setShowPopup(true);
  };

  const closePopup = () => {
    setShowPopup(false);
  };

  const handleSubmit = (name) => {
    setProfileName(name);
    setProfileTables([...profileTables, <ProfileTable key={name} profileName={name} />]);
    closePopup();
  };

  return (
    <div>
      {/* Render the ProfileTables */}
      {profileTables.map((table, index) => (
        <div key={index}>{table}</div>
      ))}

      {/* Render the popup form conditionally */}
      {showPopup && <PopupForm onSubmit={handleSubmit} onClose={closePopup} />}
      
      {/* Button to open the popup form */}
      <button onClick={openPopup}>Create Profile Table</button>
    </div>
  );
};

export default PTPopup;
